new Swiper ('.swiper-container', {
// навигация, кнопки
  navigation: {
    nextEl: '.swiper-button-forward',
    prevEl: '.swiper-button-back',
  },
// пагинатор бар
  pagination: {
    el: '.swiper-pagination',
    type: 'progressbar'
  },
// модификации 
  grabCursor: true, //  курсор-рука
  touchRatio: 5, // чувствительность перелистывания мышкой
  slideToClickedSlide: true, // перекл. при клике на элемент в слайдере
// скролл мышкой
  mousewheel: { 
    sensitivity: 1, // скорость скролла
  },
//
  autoHeight: true, // автовысота
  slidesPerView: 'auto', //кол-во показываемых слайдов
  watchOverflow: true, //если слайдов мало, - слайдер отключается
  spaceBetween: 24, // отступы между слайдами
  loop: true, // бесконечный слайдер
  
});